<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Request extends Controller {	function Request()	{		parent::Controller();		$this->load->model('Sms_model');		$this->load->plugin('randomid');	}		function index()	{/* 		$data['title'] = 'SMS Kuliah';		$data['navsection'] = 'request';		$data['subnavsection'] = '';		$data['menu'] = 'request_menu';		$data['submenu'] = 'request_submenu';		$data['content'] = 'request_content';		$this->load->view('request_template',$data); */		$data['title'] = 'SMS Kuliah';		$data['navsection'] = 'informatika';		$data['subnavsection'] = 'akun';		$data['menu'] = 'request_menu';		$data['submenu'] = 'request_submenu';		$data['queryJurusan'] = $this->Sms_model->jurusan();		$data['content'] = 'request_akun';		$this->load->view('request_template',$data);	}	function akun()	{		$data['title'] = 'SMS Kuliah';		$data['navsection'] = 'informatika';		$data['subnavsection'] = 'akun';		$data['menu'] = 'request_menu';		$data['submenu'] = 'request_submenu';		$data['queryJurusan'] = $this->Sms_model->jurusan();		$data['content'] = 'request_akun';		$this->load->view('request_template',$data);	}		function mendaftar()	{		if ($_SERVER['REQUEST_METHOD'] == 'POST'):			$jurusan = $this->input->post('jurusan');			$nama = $this->input->post('nama');			$hp = $this->input->post('hp');					$password = $this->input->post('password');			$data = array(					'nama_dosen' => trim($nama),					'hp' => $hp,					'password' => $password,					'jurusan' => $jurusan					);			$this->Sms_model->requestDosen($data);			echo "<script>window.alert('Permintaan akun Anda berhasil dikirim, silakan tunggu konfirmasi dari admin.');</script><meta http-equiv=refresh content=0;url='request'>";		else:			redirect('request');		endif;	}		function matakuliah()	{		$data['title'] = 'SMS Kuliah';		$data['navsection'] = 'informatika';		$data['subnavsection'] = 'matakuliah';		$data['menu'] = 'request_menu';		$data['submenu'] = 'request_submenu';		$data['queryJurusan'] = $this->Sms_model->jurusan();		$data['content'] = 'request_matakuliah';		$this->load->view('request_template',$data);	}	}